function [ang,rad,Ipara,Iperp]=parameters% provide the necessary parameters for mie scattering code% assuming scattering from a bubble in water; thus m = 1/1.33% also assuming scattering from red laser lambda = 632nmm = 1.6033/1.3371;lam=488e-9;R=input('Results vs radius or angle? ','s');switch lower(R)case 'radius'	rad=input('Input radius vector [r0:dr:rmax] (in MICRONS): ');	rad=rad';	x=2*pi/lam*rad*1e-6;	angd=input('Input scattered angle (in degrees): ');	ang=angd*pi/180;case 'angle'	rad=input('Input radius (in MICRONS): ');	x=2*pi/lam*rad*1e-6;	ang=[0:pi/1000:pi];otherwise	error('Unknown case, Try again!')end	tic[Ipara,Iperp] = Intensity(m, x, ang);fprintf(1,'\nElapsed Time = %.0f seconds\n\n',toc)if strcmpi(R,'radius')==1	plot(rad,Iperp,'r',rad,Ipara,'b')	%plotaspect	ylabel('Relative Intensity');xlabel('radius (µm)')	legend('Perpendicular','Parallel')else	semilogy(ang*180/pi,Iperp,'r',ang*180/pi,Ipara,'b')	%plotaspect	ylabel('Relative Intensity');xlabel('Angle (degrees)')	legend('Perpendicular','Parallel')end